document.addEventListener("astro:page-load",()=>{const e=document.getElementById("theme-toggle-dark-icon"),t=document.getElementById("theme-toggle-light-icon"),n=document.getElementById("theme-toggle");if(!e||!t||!n){console.error("Could not find theme toggle elements");return}const s=window.matchMedia("(prefers-color-scheme: dark)").matches,d=localStorage.getItem("color-theme");d==="dark"||!d&&s?(document.documentElement.classList.add("dark"),t.classList.remove("hidden")):(document.documentElement.classList.remove("dark"),e.classList.remove("hidden")),n.addEventListener("click",()=>{const o=document.documentElement.classList.toggle("dark");localStorage.setItem("color-theme",o?"dark":"light"),e.classList.toggle("hidden",o),t.classList.toggle("hidden",!o)})});
